// Extract and transform lead features
const lead = $input.first().json;

const leadFeatures = {
  lead_id: lead.id,
  source: lead.source || 'unknown',
  created_at: lead.created_at,
  last_interaction_at: lead.last_interaction_at || lead.created_at,
  property_zip: lead.property_zip || '00000',
  estimated_value: lead.estimated_value || 0,
  interaction_count: lead.interaction_count || 0,
  email_open_rate: lead.email_open_rate || 0.0,
  response_rate: lead.response_rate || 0.0,
  lead_score: lead.lead_score || 50
};

return [{
  json: {
    leadFeatures,
    isHighValue: (lead.estimated_value || 0) >= 750000,
    originalLead: lead
  }
}];

