parameters.functionCode:
// Combine results from both paths
const basicPrediction = $node['Get NBA Prediction'].json;
const enhancedPrediction = $node['GPT-5 Enhanced Prediction']?.json;
const originalLead = $node['Extract Lead Features'].json.originalLead;

const result = enhancedPrediction || basicPrediction;

return [{
  json: {
    lead_id: originalLead.id,
    action: result.action,
    confidence: result.confidence || result.ml_confidence,
    priority: result.urgency_level || (result.confidence > 0.7 ? 'high' : 'medium'),
    enhanced: !!enhancedPrediction,
    timestamp: new Date().toISOString(),
    processing_time_ms: Date.now() - new Date(originalLead.created_at).getTime()
  }
}];
