// Validate and enrich VIP lead data
const lead = $input.first().json;

// Validate VIP criteria
const isVIP = lead.estimated_value >= 750000;
const hasBasicPrediction = lead.recommended_action && lead.confidence_score;

if (!isVIP) {
  throw new Error(`Lead ${lead.id} does not meet VIP criteria ($${lead.estimated_value} < $750,000)`);
}

if (!hasBasicPrediction) {
  throw new Error(`Lead ${lead.id} missing basic NBA prediction. Run basic prediction first.`);
}

// Enrich with additional context
const enrichedLead = {
  // Core lead data
  lead_id: lead.id,
  customer_name: lead.customer_name || 'Unknown',
  property_address: `${lead.property_address || ''}, ${lead.property_zip || ''}`.trim(),
  estimated_value: lead.estimated_value,
  property_type: lead.property_type || 'Unknown',
  roof_age: lead.roof_age || 'Unknown',

  // Existing prediction
  basic_action: lead.recommended_action,
  basic_confidence: lead.confidence_score,

  // Interaction history
  total_interactions: lead.interaction_count || 0,
  last_contact: lead.last_interaction_at || lead.created_at,
  email_engagement: lead.email_open_rate || 0,
  response_rate: lead.response_rate || 0,

  // Business context
  lead_source: lead.source || 'Unknown',
  created_at: lead.created_at,
  lead_score: lead.lead_score || 50,
  assigned_to: lead.assigned_to_name || 'Unassigned',

  // Market context
  timestamp: new Date().toISOString(),
  queue_priority: 'VIP',
  enhancement_type: 'gpt5_strategic'
};

return [{ json: enrichedLead }];

// Process GPT-5 enhancement and create actionable strategy
const leadData = $node['Validate & Enrich VIP Lead'].json;
const enhancement = $input.first().json;

// Extract strategic elements
const strategy = {
  // Enhanced action
  recommended_action: enhancement.action,
  ml_confidence: enhancement.ml_confidence,
  gpt_confidence: enhancement.gpt_confidence || enhancement.ml_confidence,

  // Strategic insights
  strategic_reasoning: enhancement.reasoning || 'GPT-5 strategic analysis',
  talking_points: enhancement.talking_points || [],
  objection_handling: enhancement.objection_handling || [],

  // Personalization
  personalized_approach: enhancement.personalized_approach || 'Premium VIP treatment',
  urgency_level: enhancement.urgency_level || 'high',
  optimal_contact_time: enhancement.optimal_contact_time || 'Next business day 9-11 AM',

  // Value proposition
  value_highlights: enhancement.value_highlights || [],
  pricing_strategy: enhancement.pricing_strategy || 'Premium positioning with financing options',

  // Next steps
  immediate_actions: [
    `${enhancement.action || 'Contact'} within next 2 hours`,
    'Prepare personalized proposal with premium materials',
    'Schedule in-person consultation with senior estimator',
    'Arrange drone inspection if property > $1M'
  ],

  // Metadata
  enhancement_timestamp: new Date().toISOString(),
  model_version: 'gpt-5-turbo',
  processing_time_ms: Date.now() - new Date(leadData.timestamp).getTime()
};

// Combine with lead data
return [{
  json: {
    lead_id: leadData.lead_id,
    customer_name: leadData.customer_name,
    estimated_value: leadData.estimated_value,
    property_address: leadData.property_address,
    assigned_to: leadData.assigned_to,
    strategy,
    lead_data: leadData
  }
}];

